import { useState } from 'react'
function MessageInfo(props) {
    const { id } = useParams();
    const [message, setMessage] = useState('');

    async function Prikazi(e) {
        const res = await fetch('http://localhost:3001/message/' + id, {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                id: id
            })

        });
        if (res.status == 401) {
            alert("Napacno ime ali geslo!");
        }
        const data = await res.json();
        setMessage(data);
    }

    return (
        <div className>
            <div>
                <h5>{props.message.text}</h5>
            </div>
            <img src={"http://localhost:3001/" + props.message.path} alt={props.message.name}
                width="800px" height="600px" />
        </div>
    )
}
export default MessageInfo;


