import { useState } from 'react'
import Button from "./Button";

function AddMessage(props) {
    const [text, setText] = useState('');
    const [file, setFile] = useState('');

    async function AddMessage(e) {
        e.preventDefault();
        const time = Date.now();
        const res = await fetch('http://localhost:3001/message/', {
            method: 'POST',
            credentials: 'include',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                text: text,
                path: file,
                likes: 0,
                views: 0,
                time: time
            })
        });
        const data = await res.json();
        setText("");
        setFile("");
    }

    return (
        <form className="form-group" onSubmit={AddMessage}>
            <input type="text" className="form-control" name="text" placeholder="Besedilo" value={text} onChange={(e) => { setText(e.target.value) }} required="" />
            <label>Izberi sliko</label><br/>
            <input type="file" id="file" onChange={(e) => { setFile(e.target.files[0]) }} required="" />
            <br/>
            <Button text="Dodaj" />
        </form>
    )
}

export default AddMessage;
